         this.temporaryStatSet(1320019, skilllevel, effect.getDuration(), statups, true);
         Map<SecondaryStatFlag, Integer> statups2 = new HashMap<>();
         statups2.put(SecondaryStatFlag.ReincarnationActivate, z);
