   public void removePet(MaplePet pet, boolean shiftLeft) {
      int slot = -1;

      for (int i = 0; i < 3; i++) {
         if (this.pets[i] != null && this.pets[i].getUniqueId() == pet.getUniqueId()) {
            this.pets[i] = null;
            slot = i;
            break;
         }
      }

      if (shiftLeft && slot > -1) {
         for (int ix = slot; ix < 3; ix++) {
            if (ix != 2) {
               this.pets[ix] = this.pets[ix + 1];
            } else {
               this.pets[ix] = null;
            }
         }
      }
   }

