               Calendar cal = Calendar.getInstance();
               cal.setTime(lastTime);
               cal.set(7, 2);
               cal.set(10, 0);
               cal.set(12, 0);
               cal.set(13, 0);
               cal.set(14, 0);
               cal.set(11, 0);
               cal.set(5, cal.getTime().getDate() + 7);
